import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { dodajOcjenu } from '../redux/ocjene/actions';
import formStyles from './Form.module.css';

const AddOcjenuForm = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [ucenik, setUcenik] = useState('');
  const [nastavnik, setNastavnik] = useState('');
  const [datum, setDatum] = useState('');
  const [ocj, setOcj] = useState('');
  const [razred, setRazred] = useState('');
  const [odjeljenje, setOdjeljenje] = useState('');
  const [predmet, setPredmet] = useState('');
  const [backdrop, setBackdrop] = useState('');

  const predmeti = useSelector((state) => state.predmeti);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(dodajOcjenu({ ucenik, nastavnik, datum, ocj, razred })).then(() =>
      navigate(-1)
    );
  };
  return (
    <>
      {!backdrop && (
        <div
          className={formStyles.backdrop}
          onClick={() => {
            setBackdrop(true);
          }}
        ></div>
      )}
      <form onSubmit={submitHandler} className={formStyles['form-control']}>
        <div>
          <ul>
            <li>
              <label>Odaberi odjeljenje</label>
              <input
                value={odjeljenje}
                onChange={(e) => setOdjeljenje(e.target.value)}
                type="text"
                placeholder="Odjeljenje"
              />
            </li>
            <li>
              <label>Odaberi ucenika</label>
              <input
                value={ucenik}
                onChange={(e) => setUcenik(e.target.value)}
                type="text"
                placeholder="Ucenik"
              />
            </li>
            <li>
              <label>Odaberi nastavnika</label>
              <input
                value={nastavnik}
                onChange={(e) => setNastavnik(e.target.value)}
                type="text"
                placeholder="Nastavnik"
              />
            </li>
            <li>
              <label>Odaberi predmet</label>
              <select>
                {predmeti.forEach((predmet) => {
                  <option>{predmet.naziv}</option>;
                })}
              </select>
              <input
                value={predmet}
                onChange={(e) => setPredmet(e.target.value)}
                type="text"
                placeholder="Predmet"
              />
            </li>
            <li>
              <label>Datum</label>
              <input
                value={datum}
                onChange={(e) => setDatum(e.target.value)}
                type="text"
                placeholder="Datum"
              />
            </li>
            <li>
              <label>Ocjena</label>
              <input
                value={ocj}
                onChange={(e) => setOcj(e.target.value)}
                type="number"
                placeholder="Ocjena"
              />
            </li>
            <li>
              <label>Razred</label>
              <input
                value={razred}
                onChange={(e) => setRazred(e.target.value)}
                placeholder="Razred"
              />
            </li>
          </ul>
        </div>
        <button>Finnish adding</button>
      </form>
    </>
  );
};

export default AddOcjenuForm;
