import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { Link, useNavigate } from 'react-router-dom';
import { authUser } from '../redux/authUser/actions';
import formStyles from './Form.module.css';

const LoginForm = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [ime, setIme] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState({ ime: '', password: '' });

  const submitHandler = (e) => {
    e.preventDefault();
    if (ime.trim().length === 0) {
      setError({ ...error, ime: 'Must enter a value' });
    }
    dispatch(authUser({ ime, password }));
  };

  const handleChangeIme = (e) => {
    e.preventDefault();
    if (e.target.value.includes('_')) {
      setError({ ...error, name: 'You cannot use an underscore' });
    } else {
      setError({ ...error, name: '' });
    }
    setIme(e.target.value);
  };

  const handleImeOnBlur = () => {
    if (ime.trim().length === 0) {
      setError({ ...error, name: 'Please fill out this field' });
    } else {
      setError({ ...error, name: '' });
    }
  };

  return (
    <>
      {<div className={formStyles.backdrop}></div>}
      <form onSubmit={submitHandler} className={formStyles['form-control']}>
        <h1>Login</h1>
        <div>
          <ul>
            <li>
              <label>Ime</label>
              <input
                value={ime}
                onChange={handleChangeIme}
                onBlur={handleImeOnBlur}
                type="text"
                placeholder="Ime"
              />
              {error.ime && (
                <span style={{ color: 'red' }} htmlFor="ime">
                  {error.ime}
                </span>
              )}
            </li>
            <li>
              <label>Password</label>
              <input
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                type="password"
                placeholder="Password"
              />
              {error.password && (
                <span style={{ color: 'red' }} htmlFor="ime">
                  {error.password}
                </span>
              )}
            </li>
          </ul>
        </div>
        <button>Login</button>
        <hr></hr>
        <Link to="/createUser">Don't have an account?</Link>
      </form>
    </>
  );
};

export default LoginForm;
