import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Link, useLocation, useNavigate, useParams } from 'react-router-dom';
import styles from './Razredi.module.css';
import { ucitajRazredeIzSkolskeGodine } from '../redux/odjeljenjaRazredi/actions';
import RazredRow from './RazredRow';
import { ucitajSveSkolskeGodine } from '../redux/skolskeGodine/actions';

const Razredi = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { pathname } = useLocation();
  const { idSkolskaGodina } = useParams();
  const [idSkGod, setIdSkGod] = useState(idSkolskaGodina);
  const razredi = useSelector((state) => state.razredi.items);
  const skolskeGodine = useSelector((state) => state.skolskeGodine.items);
  console.log(skolskeGodine.length);

  useEffect(() => {
    if (skolskeGodine.length < 1) {
      dispatch(ucitajSveSkolskeGodine());
    }
  }, [skolskeGodine.length, idSkGod]);

  useEffect(() => {
    if (skolskeGodine.length > 0 && idSkolskaGodina === undefined) {
      navigate(pathname + '/' + skolskeGodine[0].idSkolskaGodina || idSkGod);
    }
  }, [skolskeGodine.length, idSkGod]);

  useEffect(() => {
    if (idSkolskaGodina) {
      dispatch(ucitajRazredeIzSkolskeGodine(+idSkolskaGodina));
    }
  }, [idSkolskaGodina]);

  const submitHandler = (e) => {
    e.preventDefault();
    const idSkolskaGodina = e.target.idSkolskaGodina.value;
    setIdSkGod(idSkolskaGodina);
    pathname = null;
    dispatch(ucitajRazredeIzSkolskeGodine(idSkGod));
    navigate('razredi/' + idSkGod);
  };

  return (
    <div>
      <button
        onClick={() => {
          navigate('/razredi/add-razred');
        }}
      >
        Add razred
      </button>
      <form onSubmit={submitHandler}>
        <select name="idSkolskaGodina">
          {skolskeGodine.length > 0 &&
            skolskeGodine.map((sg) => {
              return (
                <option value={sg.idSkolskaGodina} key={sg.idSkolskaGodina}>
                  {sg.skolskaGodina}
                </option>
              );
            })}
        </select>
        <button>Pretra≈æi</button>
      </form>
      <div className={styles['razredi-container']}>
        {razredi &&
          razredi.map((razredi) => {
            return (
              <div key={razredi.idOdjeljenja} className={styles.razredi}>
                <RazredRow
                  id={razredi.idOdjeljenja}
                  idNastavnikRazrednik={razredi.idNastavnikRazrednik}
                  razred={razredi.razred}
                  oznakaOdjeljenja={razredi.oznakaOdjeljenja}
                  razrednik={razredi.razrednik}
                />
                <Link to={'/razredi/' + razredi.idOdjeljenja}>Details</Link>
              </div>
            );
          })}
      </div>
    </div>
  );
};

export default Razredi;
