import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
  createSearchParams,
  useNavigate,
  useParams,
  useSearchParams,
} from 'react-router-dom';
import { Tab, Tabs as TabsComponent, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import { ucitajUcenika } from '../redux/ucenici/actions';

const Tabs = ({ razredi = [], predmeti = [] }) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { idUcenik } = useParams();

  const [ucenik] = useSelector((state) =>
    state.ucenici.items.filter((ucenik) => ucenik.idUcenik === +idUcenik)
  );
  const [searchParams, setSearchParams] = useSearchParams();
  const [razredID, setRazredID] = useState(razredi[0].id_razred);

  console.log(razredi);
  useEffect(() => {
    dispatch(ucitajUcenika(+idUcenik, razredID));
  }, [dispatch, razredID, idUcenik]);

  console.log(razredID);

  /* const idOdjeljenja = searchParams.get('idOdjeljenja'); */

  const goToPosts = (params) =>
    navigate({
      /* pathname: '/posts', */
      search: `?${createSearchParams(params)}`,
    });

  return (
    <>
      <TabsComponent>
        <TabList>
          {razredi.map((razred, i) => (
            <Tab
              key={razred.id_razred}
              onClick={() => (
                goToPosts({ idOdjeljenja: razred.id_razred }),
                setRazredID(razred.id_razred)
              )}
            >
              {razred.razred}
            </Tab>
          ))}
        </TabList>
        {/*   {razredi.map((razred, i) => (
          <TabPanel key={i}>
            <h2>Razrednik: {razred.razrednik}</h2>
            <TabsComponent>
                  <TabList>
