                  predmeti.map((predmet, i) => {
                    if (razred.razred === predmet.razred) {
                      return (
                        <Tab key={predmet.id_predmet}>{predmet.naziv}</Tab>
                      );
                    }
                    return [];
                  })}
              </TabList>
              {predmeti.map((predmet, i) => {
                if (razred.razred === predmet.razred) {
                  return (
                    <TabPanel key={i}>
                      <h2>Ocjena: {i}</h2>
                    </TabPanel>
                  );
                }
                return [];
              })} 
            </TabsComponent>
          </TabPanel>
        ))} */}
      </TabsComponent>
    </>
  );
};

export default Tabs;
