import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useParams, useNavigate, Link, Outlet } from 'react-router-dom';
import { ucitajUcenikoveRazrede } from '../redux/odjeljenjaRazredi/actions';
import { Tab, Tabs as TabsComponent, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';

const Razred = (props) => {
  const { idRazred, idUcenik } = useParams();
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { loading } = useSelector((state) => state.razredi);
  const ucenikoviRazredi = useSelector((state) => state.razredi.items);

  useEffect(() => {
    if (ucenikoviRazredi.length < 1) {
      dispatch(ucitajUcenikoveRazrede(+idUcenik)).then((ucenikoviRazredi) => {
        /* navigate(
          '/ucenici/' +
            idUcenik +
            '/' +
            ucenikoviRazredi[0].idRazred +
            '/predmeti'
        ); */
      });
    }
  }, [dispatch, idUcenik, ucenikoviRazredi]);

  if (loading) {
    return <h1>Loading...</h1>;
  }
  if (!ucenikoviRazredi) {
    return <h1>Ucenikovi razredi doesn't exist</h1>;
  }

  console.log(ucenikoviRazredi);
  console.log(idRazred);

  return (
    <>
      <TabsComponent>
        <TabList>
          {ucenikoviRazredi.map((razred, i) => (
            <Tab key={razred.idRazred} active={'true'}>
              <Link
                to={
                  '/ucenici/' + idUcenik + '/' + razred.idRazred + '/predmeti'
                }
              >
                {razred.razred}
              </Link>
            </Tab>
          ))}
        </TabList>
        {ucenikoviRazredi.map((razred, i) => (
          <TabPanel key={razred.idRazred}>
            Razrednik je: {razred.razrednik}
          </TabPanel>
        ))}
      </TabsComponent>
      <Outlet />
    </>
  );
};

export default Razred;
