import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  ucitajPredmet,
  obrisiPredmet,
  urediPredmet,
} from '../redux/predmeti/actions';
import { useParams, useNavigate, Link } from 'react-router-dom';
import PredmetRow from './PredmetRow';
import styles from './Nastavnici.module.css';
import PredmetForm from './PredmetForm';

const Predmet = (props) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const { idPredmet } = useParams();
  console.log(idPredmet);
  const [editing, setEditing] = useState(false);
  const [predmet] = useSelector((state) =>
    state.predmeti.items.filter((pred) => pred.idPredmet === +idPredmet)
  );
  const { loading } = useSelector((state) => state.predmeti);
  console.log(1, predmet);
  const [naziv, setNaziv] = useState('');

  useEffect(() => {
    if (predmet) {
      dispatch(ucitajPredmet(+idPredmet));
    }
  }, []);

  useEffect(() => {
    if (!predmet.naziv) setNaziv(predmet.naziv);
  }, [predmet.naziv]);

  const editingHandler = (e) => {
    e.preventDefault();
    dispatch(urediPredmet({ idPredmet, naziv })).then(() =>
      navigate(`/predmeti/${idPredmet}`)
    );
    setEditing(false);
  };

  if (loading) {
    return <h1>Loading...</h1>;
  }
  if (!predmet) {
    return <h1>Predmet doesn't exist</h1>;
  }
  return !editing ? (
    <div key={predmet.idPredmet} className={styles.nastavnici}>
      <PredmetRow id={predmet.idPredmet} naziv={naziv} />
      {idPredmet && (
        <>
          <button
            onClick={() => {
              dispatch(obrisiPredmet(+idPredmet)).then(() =>
                navigate('/predmeti')
              );
            }}
          >
            Delete
          </button>
          <button onClick={() => setEditing(true)}>Edittttt</button>
        </>
      )}
    </div>
  ) : (
    <PredmetForm
      value={naziv}
      setValue={setNaziv}
      onSubmit={editingHandler}
      buttonText={'Done editing'}
    />
  );
};

export default Predmet;
