import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { dodajUsera } from '../redux/users/actions';
import formStyles from './Form.module.css';

const RegisterUser = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [ime, setIme] = useState('');
  const [password, setPassword] = useState('');
  const [backdrop, setBackdrop] = useState('');
  const [error, setError] = useState(null);
  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(dodajUsera({ ime, password }));
  };

  const handleChange = (e) => {
    e.preventDefault();
    console.log(e.target.value);
    if (e.target.value.includes('_')) setError('You cannot use an underscore');
    setIme(e.target.value);
  };
  return (
    <>
      {!backdrop && <div className={formStyles.backdrop}></div>}
      <form className={formStyles['form-control']} onSubmit={submitHandler}>
        <div>
          <ul>
            <li style={{ display: 'block' }}>
              <label>Ime</label>
              <input
                value={ime}
                onChange={handleChange}
                type="text"
                placeholder="Ime"
              />
              {error && (
                <label style={{ color: 'red' }} htmlFor="message">
                  {error}
                </label>
              )}
            </li>
            <li>
              <label>Password</label>
              <input
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                type="password"
                placeholder="Password"
              />
            </li>
          </ul>
        </div>
        <button type="submit">Sign up</button>
      </form>
    </>
  );
};

export default RegisterUser;
