import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { dodajPredmet, ucitajSvePredmete } from '../redux/predmeti/actions';
import { Link, useNavigate } from 'react-router-dom';
import styles from './Nastavnici.module.css';
import PredmetRow from './PredmetRow';
import PredmetForm from './PredmetForm';

const Predmeti = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const predmeti = useSelector((state) => state.predmeti);
  const [posting, setPosting] = useState(false);
  const [predmet, setPredmet] = useState('');

  useEffect(() => {
    dispatch(ucitajSvePredmete());
  }, []);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(dodajPredmet({ naziv: predmet })).then(() =>
      navigate('/predmeti')
    );
    setPosting(false);
    navigate('/predmeti');
  };

  return posting ? (
    <PredmetForm
      value={predmet}
      setValue={setPredmet}
      onSubmit={submitHandler}
      placeholder="Predmet"
    />
  ) : (
    <div>
      <button
        onClick={() => {
          setPosting(true);
          navigate('/predmeti/add-predmet');
        }} /* to={`?posting=${!posting}`} */
      >
        Add predmet
      </button>
      {predmeti &&
        predmeti.items.map((pred) => {
          return (
            <div key={pred.idPredmet} className={styles.nastavnici}>
              <PredmetRow id={pred.idPredmet} naziv={pred.naziv} />
              <Link to={'/predmeti/' + pred.idPredmet}>Details</Link>
            </div>
          );
        })}
    </div>
  );
};

export default Predmeti;
