import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { dodajUsera } from '../redux/users/actions';
import formStyles from './Form.module.css';

const RegisterUser = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [ime, setIme] = useState('');
  const [password, setPassword] = useState('');
  const [backdrop, setBackdrop] = useState('');
  const [error, setError] = useState({ email: '', password: '' });

  const submitHandler = (e) => {
    e.preventDefault();

    dispatch(dodajUsera({ ime, password }));
  };

  const handleChangeIme = (e) => {
    e.preventDefault();

    if (e.target.value.includes('_')) {
      setError({ ...error, email: 'You cannot use an underscore' });
    }
    setIme(e.target.value);
  };

  return (
    <>
      {!backdrop && <div className={formStyles.backdrop}></div>}
      <form className={formStyles['form-control']} onSubmit={submitHandler}>
        <div>
          <ul>
            <li>
              <label>Ime</label>
              <input
                value={ime}
                name="ime"
                onChange={handleChangeIme}
                type="text"
                placeholder="Ime"
              />
              {error.email && (
                <label style={{ color: 'red' }} htmlFor="ime">
                  {error.email}
                </label>
              )}
            </li>
            <li>
              <label>Password</label>
              <input
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                type="password"
                placeholder="Password"
              />
            </li>
          </ul>
        </div>
        <button type="submit">Sign up</button>
      </form>
    </>
  );
};

export default RegisterUser;
