import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate, useParams } from 'react-router-dom';
import {
  ucitajNastavnika,
  ucitajSveNastavnike,
} from '../redux/nastavnici/actions';
import { dodajOcjenu } from '../redux/ocjene/actions';
import formStyles from './Form.module.css';

const AddOcjenuForm = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { idUcenik } = useParams();
  const [ucenik, setUcenik] = useState('');
  const [nastavnik, setNastavnik] = useState('');
  const [datum, setDatum] = useState('');
  const [ocj, setOcj] = useState('');
  const [razred, setRazred] = useState('');
  const [backdrop, setBackdrop] = useState('');

  const ucenici = useSelector((state) => state.ucenici);
  const predmeti = useSelector((state) => state.predmeti);
  const nastavnici = useSelector((state) => state.nastavnici.items);
  const ucenikoviRazredi = useSelector((state) => state.razredi.items);
  console.log(ucenikoviRazredi);

  useEffect(() => {
    if (nastavnici.length < 1) {
      console.log('ovdje sam');
      dispatch(ucitajSveNastavnike());
    }
  }, []);

  const submitHandler = (e) => {
    e.preventDefault();
    console.log(e.target.elements);
    dispatch(dodajOcjenu({ ucenik, nastavnik, datum, ocj, razred })).then(() =>
      navigate(-1)
    );
  };
  return (
    <>
      {!backdrop && (
        <div
          className={formStyles.backdrop}
          onClick={() => {
            setBackdrop(true);
          }}
        ></div>
      )}
      <form onSubmit={submitHandler} className={formStyles['form-control']}>
        <div>
          <ul>
            <li>
              <label>Odaberi ucenikov razred</label>
              <select name="idRazred">
                {ucenikoviRazredi.map((razred) => (
                  <option value={razred.idRazred} key={razred.idRazred}>
                    {razred.razred}
                  </option>
                ))}
              </select>
            </li>
            <li>
              <label>Odaberi ucenika</label>
              <select name="idUcenik">
                {ucenici.items.map((ucenik) => (
                  <option value={ucenik.idUcenik} key={ucenik.idUcenik}>
                    {ucenik.ime} {ucenik.prezime}
                  </option>
                ))}
              </select>
            </li>
            <li>
              <label>Odaberi nastavnika</label>
              <select name="idNastavnik">
                {nastavnici.map((nastavnik) => (
                  <option
                    value={nastavnik.idNastavnik}
                    key={nastavnik.idNastavnik}
                  >
                    {nastavnik.ime} {nastavnik.prezime}
                  </option>
                ))}
              </select>
            </li>
            <li>
              <label htmlFor="predmeti">Odaberi predmet</label>
              <select name="idPredmet">
                {predmeti.items.map((predmet) => (
                  <option value={predmet.idPredmet} key={predmet.idPredmet}>
                    {predmet.predmet}
                  </option>
                ))}
              </select>
            </li>
            <li>
              <label>Datum</label>
              <input
                value={datum}
                onChange={(e) => setDatum(e.target.value)}
                type="text"
                placeholder="Datum"
              />
            </li>
            <li>
              <label>Ocjena</label>
              <input
                value={ocj}
                onChange={(e) => setOcj(e.target.value)}
                type="number"
                placeholder="Ocjena"
              />
            </li>
          </ul>
        </div>
        <button>Finnish adding</button>
      </form>
    </>
  );
};

export default AddOcjenuForm;
