const PredmetForm = ({
  value = '',
  setValue,
  onSubmit,
  placeholder = '',
  buttonText = 'Submit',
}) => {
  return (
    <form onSubmit={onSubmit} className={formStyles['form-control']}>
      <div>
        <ul>
          <li>
            <input
              value={value}
              onChange={(e) => setValue(e.target.value)}
              type="text"
              placeholder={placeholder}
            />
          </li>
        </ul>
      </div>
      <button>{buttonText}</button>
    </form>
  );
};

export default PredmetForm;
