import React, { useEffect, useState } from 'react';
import formStyles from './Form.module.css';
import { useSelector, useDispatch } from 'react-redux';
import { useParams, useNavigate, useSearchParams } from 'react-router-dom';
import {
  dodajPredmet,
  ucitajSvePredmete,
  urediPredmet,
} from '../redux/predmeti/actions';

const PredmetForm = (props) => {
  const { idPredmet } = useParams();

  const [searchParams, setSearchParams] = useSearchParams({
    posting: props.posting,
  });
  console.log(searchParams.get('posting'));
  const [editing, setEditing] = useState(props.editing);
  const [posting, setPosting] = useState(props.posting);
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [naziv, setNaziv] = useState(props.naziv);

  const [predmet, setPredmet] = useState('');
  console.log(predmet);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(dodajPredmet({ naziv: predmet })).then(() =>
      navigate('/predmeti')
    );

    setPosting(false);
    navigate('/predmeti');
  };

  const editingHandler = (e) => {
    e.preventDefault();
    dispatch(urediPredmet({ idPredmet, naziv })).then(() =>
      navigate(`/predmeti/${idPredmet}`)
    );
  };

  if (editing) {
    return (
      <div>
        <form onSubmit={editingHandler}>
          <input
            value={naziv}
            onChange={(e) => {
              setNaziv(e.target.value);
            }}
          />
          <button>Done editing</button>
        </form>
      </div>
    );
  }

  if (posting) {
    return (
      <form onSubmit={submitHandler} className={formStyles['form-control']}>
        <div>
          <ul>
            <li>
              <input
                value={predmet}
                onChange={(e) => setPredmet(e.target.value)}
                type="text"
                placeholder="naziv predmeta"
                name="predmet"
              />
            </li>
          </ul>
        </div>
        <button>Add predmet</button>
      </form>
    );
  }
};

export default PredmetForm;
