import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { dodajPredmet, ucitajSvePredmete } from '../redux/predmeti/actions';
import { Link, useNavigate, createSearchParams } from 'react-router-dom';
import styles from './Nastavnici.module.css';
import PredmetRow from './PredmetRow';
import PredmetForm from './PredmetForm';

const Predmeti = () => {
  const navigate = useNavigate();
  const predmeti = useSelector((state) => state.predmeti);
  const dispatch = useDispatch();
  const [posting, setPosting] = useState(false);
  console.log(12, posting);
  useEffect(() => {
    dispatch(ucitajSvePredmete());
  }, []);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(dodajPredmet({ naziv: predmet })).then(() =>
      navigate('/predmeti')
    );

    setPosting(false);
    navigate('/predmeti');
  };

  return posting ? (
    <PredmetForm posting={true} setPosting={setPosting} />
  ) : (
    <div>
      <Link onClick={() => setPosting(true)} to={`?posting=${!posting}`}>
        Add predmet
      </Link>
      {predmeti &&
        predmeti.items.map((pred) => {
          return (
            <div key={pred.idPredmet} className={styles.nastavnici}>
              <PredmetRow id={pred.idPredmet} naziv={pred.naziv} />
              <Link to={'/predmeti/' + pred.idPredmet}>Details</Link>
            </div>
          );
        })}
    </div>
  );
};

export default Predmeti;
