import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useParams, useNavigate } from 'react-router-dom';
import { ucitajPredmet, urediOcjenu } from '../redux/ocjene/actions';
import formStyles from './Form.module.css';

const EditOcjenuForm = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { idOcjena } = useParams();
  const [ocjena] = useSelector((state) =>
    state.ocjene.items.filter((pred) => pred.idOcjena === +idOcjena)
  );

  const [naziv, setNaziv] = useState('');

  useEffect(() => {
    if (!ocjena) {
      dispatch(ucitajPredmet(+idOcjena));
    }
  }, []);

  useEffect(() => {
    if (ocjena) setNaziv(ocjena.ucenik);
  }, [ocjena]);

  const editingHandler = (e) => {
    e.preventDefault();
    dispatch(urediOcjenu({ idOcjena, naziv })).then(() =>
      navigate(`/ocjena/${idOcjena}`)
    );
  };

  return (
    <form onSubmit={editingHandler} className={formStyles['form-control']}>
      <div>
        <ul>
          <li>
            <input
              value={naziv}
              onChange={(e) => setNaziv(e.target.value)}
              type="text"
            />
          </li>
        </ul>
      </div>
      <button>Done editing</button>
    </form>
  );
};

export default EditOcjenuForm;
