import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { ucitajUcenikoveRazrede } from '../redux/odjeljenjaRazredi/actions';
import { Tab, Tabs as TabsComponent, TabList, TabPanel } from 'react-tabs';

const Razred = (props) => {
  const { idRazred } = useParams();
  const { idUcenik } = useParams();
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { loading } = useSelector((state) => state.razredi);
  const [ucenikoviRazredi] = useSelector((state) =>
    state.ucenici.items.filter((ucenik) => ucenik.idRazred === +idRazred)
  );

  useEffect(() => {
    if (!ucenikoviRazredi) {
      dispatch(ucitajUcenikoveRazrede(+idUcenik));
    }
  }, [dispatch, idUcenik, ucenikoviRazredi]);

  useEffect(() => {
    navigate('/ucenici/' + +idUcenik + ucenikoviRazredi[0].idRazred);
  }, []);

  if (loading) {
    return <h1>Loading...</h1>;
  }
  if (!ucenikoviRazredi) {
    return <h1>Ucenikovi razredi doesn't exist</h1>;
  }

  return (
    <TabsComponent>
      <TabList>
        {ucenikoviRazredi.map((razred, i) => (
          <Tab key={razred.idRazred} active={'true'}>
            <Link to={'/ucenici/' + idUcenik + '/' + razred.idRazred}>
              {razred.razred}
            </Link>
          </Tab>
        ))}
      </TabList>
      {ucenikoviRazredi.map((razred, i) => (
        <TabPanel key={razred.idRazred}>
          Razrednik je: {razred.razrednik}
        </TabPanel>
      ))}
    </TabsComponent>
  );
};

export default Razred;
