const { getAuthTokens } = require('.');

const fetchInstance = async (url, fetchInit) => {
  let newFetchInit = undefined;
  if (fetchInit) {
    newFetchInit = { ...fetchInit };
    if (newFetchInit.headers) {
      newFetchInit.headers = {
        ...newFetchInit.headers,
        Authorization: `Bearer ${getAuthTokens().token}`,
      };
    } else {
      newFetchInit['headers'] = {
        Authorization: `Bearer ${getAuthTokens().token}`,
      };
    }
  } else {
    newFetchInit = {
      headers: {
        Authorization: `Bearer ${getAuthTokens().token}`,
      },
    };
  }

  let response = await fetch(url, newFetchInit);
  console.log(response, response.ok, response.status, response.status === 401);
  if (!response.ok) {
    if (response.statusText === '401') {
      response = await fetch('http://localhost:3001/login/refresh', {
        body: JSON.stringify({ refreshToken: getAuthTokens().refreshToken }),
      });
    }
  }

  console.log(response);
};

export default fetchInstance;
