import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
  createSearchParams,
  useNavigate,
  useParams,
  useSearchParams,
} from 'react-router-dom';
import { Tab, Tabs as TabsComponent, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import { ucitajUcenika } from '../redux/ucenici/actions';

const Tabs = ({ razredi = [], predmeti }) => {
  console.log(predmeti, razredi);
  const dispatch = useDispatch();
  const { idUcenik } = useParams();
  console.log(idUcenik);

  /*  const [ucenik] = useSelector((state) =>
    state.ucenici.items.filter((ucenik) => ucenik.idUcenik === +idUcenik)
  ); */
  /* useEffect(() => {
  if(!razredi){
    dispatch(ucitajUcenika)
  }
}, []) */

  const [searchParams, setSearchParams] = useSearchParams();

  const razredId = searchParams.get('razred');
  console.log(razredId);
  useEffect(() => {
    dispatch(ucitajUcenika(idUcenik, razredId));
  }, []);

  const navigate = useNavigate();

  const goToPosts = (params) =>
    navigate({
      /* pathname: '/posts', */
      search: `?${createSearchParams(params)}`,
    });
  return (
    <>
      <TabsComponent>
        <TabList>
          {razredi.map((razred, i) => (
            <Tab
              key={razred.id_razred}
              onClick={() => goToPosts({ razred: razred.id_razred })}
            >
              {razred.razred}
            </Tab>
          ))}
        </TabList>
        {razredi.map((razred, i) => (
          <TabPanel key={i}>
            <h2>Razrednik: {razred.razrednik}</h2>
            <TabsComponent>
              <TabList>
                {razredId &&
                  predmeti.map((predmet, i) => {
                    if (razred.razred === predmet.razred) {
                      return (
                        <Tab key={predmet.id_predmet}>{predmet.naziv}</Tab>
                      );
                    }
                    return [];
                  })}
              </TabList>
              {predmeti.map((predmet, i) => {
                if (razred.razred === predmet.razred) {
                  return (
                    <TabPanel key={i}>
                      <h2>Ocjena: {i}</h2>
                    </TabPanel>
                  );
                }
                return [];
              })}
            </TabsComponent>
          </TabPanel>
        ))}
      </TabsComponent>
    </>
  );
};

export default Tabs;
