import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Outlet, useLocation, useNavigate, useParams } from 'react-router-dom';
import { Tabs as TabsComponent } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import {
  obrisiOcjenu,
  ucitajOcjeneIzPredmeta,
  urediOcjenu,
} from '../redux/ocjene/actions';
import OcjenaRow from './OcjenaRow';
import styles from './Nastavnici.module.css';
import {
  dodajZakljucnuOcjenu,
  ucitajZakljucnuOcjenu,
} from '../redux/zakljucnaOcjena/actions';

const UcenikoveOcjene = (props) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { idUcenik, idRazred, idPredmet } = useParams();
  const { pathname } = useLocation();
  const { loading } = useSelector((state) => state.ocjene);
  const [dodajOcjenu, setDodajOcjenu] = useState(false);
  const [zakljOcj, setZakljOcj] = useState('');
  const ucenikoveOcjene = useSelector((state) => state.ocjene.items);
  const ucenikoviRazredi = useSelector((state) => state.razredi.items);
  const [currentRazred] = useSelector((state) =>
    state.razredi.items.filter((razred) => razred.idRazred === +idRazred)
  );
  const zakljucnaOcjena = useSelector((state) => state.zakljucneOcjene.items);
  console.log(1111, zakljucnaOcjena);
  const pojedinacneOcjene = ucenikoveOcjene.map((ocjena) => ocjena.ocjena);
  const prosjek = pojedinacneOcjene.reduce((total, curr) => {
    return +total + +curr / pojedinacneOcjene.length;
  }, 0);
  const allRazredsIds = ucenikoviRazredi.map((r) => {
    return r.idRazred;
  });
  const indexOfFirstElement = allRazredsIds.indexOf(+idRazred);

  useEffect(() => {
    dispatch(ucitajOcjeneIzPredmeta(+idUcenik, +idRazred, +idPredmet));
  }, [idPredmet, zakljucnaOcjena, zakljucnaOcjena.length]);

  useEffect(() => {
    if (dodajOcjenu) {
      navigate(pathname + '/add-ocjenu');
    }
  }, [dodajOcjenu]);

  useEffect(() => {
    if (idUcenik && idPredmet && currentRazred.idSkolskaGodina) {
      dispatch(
        ucitajZakljucnuOcjenu(
          idUcenik,
          idPredmet,
          currentRazred.idSkolskaGodina
        )
      );
    }
  }, [
    idUcenik,
    idPredmet,
    currentRazred.idSkolskaGodina,
    zakljucnaOcjena.length,
    zakljucnaOcjena.zakljucnaOcjena,
  ]);

  const editOcjenaHandler = (id, datum, nastavnik, predmet, ocjena, opis) => {
    const ocjenaForEdit = { id, datum, nastavnik, predmet, ocjena, opis };
    dispatch(urediOcjenu(ocjenaForEdit));
  };

  const deleteOcjenaHandler = (idOcjena) => {
    dispatch(obrisiOcjenu(idOcjena));
    navigate(-1);
  };

  const zakljucnaOcjenaSubmitHandler = (e) => {
    if (ucenikoveOcjene) {
      console.log(e.target.zakljucnaOcjena.value);
      e.preventDefault();
      const zakljOcjena = {
        idUcenik,
        idPredmet,
        idSkolskaGodina: currentRazred.idSkolskaGodina,
        zakljucnaOcjena: zakljOcj,
      };
      console.log(zakljOcjena);
      dispatch(dodajZakljucnuOcjenu(zakljOcjena));
    }
    /* navigate(0); */
  };

  if (loading) {
    return <h1>Loading...</h1>;
  }

  if (ucenikoveOcjene.length < 1) {
    return (
      <>
        <h1>Nema ocjena</h1>
        {indexOfFirstElement < 1 && (
          <button
            onClick={() => {
              setDodajOcjenu(true);
            }}
          >
            Dodaj ocjenu
          </button>
        )}
      </>
    );
  }

  return (
    <>
      {indexOfFirstElement < 1 && (
        <button
          onClick={() => {
            setDodajOcjenu(true);
          }}
        >
          Dodaj ocjenu
        </button>
      )}
      <TabsComponent className={styles['ocjene-container']}>
        {ucenikoveOcjene.map((ocjena, i) => (
          <div key={ocjena.idOcjena}>
            <OcjenaRow
              id={ocjena.idOcjena}
              datum={String(ocjena.datum).split('T')[0]}
              predmet={ocjena.predmet}
              nastavnik={ocjena.nastavnik}
              ocjena={ocjena.ocjena}
              razred={ocjena.razred}
              opis={ocjena.opis}
              onEditOcjenaHandler={editOcjenaHandler}
              onDeleteOcjenaHandler={deleteOcjenaHandler}
              indexOfFirstElement={indexOfFirstElement}
            />
          </div>
        ))}
      </TabsComponent>

      <label>Prosjek ocjena</label>
      <input defaultValue={prosjek.toFixed(2)}></input>
      <form onSubmit={zakljucnaOcjenaSubmitHandler}>
        <label>Zakljucna ocjena</label>
        {idPredmet && zakljucnaOcjena.length > 0 ? (
          <input defaultValue={zakljucnaOcjena[0].zakljucnaOcjena}></input>
        ) : (
          <>
            <input
              name="zakljucnaOcjena"
              onChange={(e) => setZakljOcj(e.target.value)}
            />
            <button type="submit">Submit</button>
          </>
        )}
      </form>
      <Outlet />
    </>
  );
};

export default UcenikoveOcjene;
