import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate, useParams } from 'react-router-dom';
import {
  dodajUcenikaURazred,
  ucitajSveUcenike,
} from '../redux/ucenici/actions';
import formStyles from './Form.module.css';

const AddUcenikURazredForm = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { idRazred } = useParams();
  const [backdrop, setBackdrop] = useState('');
  const ucenici = useSelector((state) => state.ucenici.items);

  useEffect(() => {
    dispatch(ucitajSveUcenike());
  }, []);

  const backdropHandler = () => {
    setBackdrop(true);
    navigate(-1);
  };

  const submitHandler = (e) => {
    e.preventDefault();
    console.log('hereeeeeeee');
    const idUcenik = e.target.idUcenik.value;
    dispatch(dodajUcenikaURazred(+idRazred, idUcenik));
  };
  return (
    ucenici && (
      <>
        {!backdrop && (
          <div
            className={formStyles.backdrop}
            onClick={() => {
              backdropHandler();
            }}
          ></div>
        )}
        <from onSubmit={submitHandler} className={formStyles['form-control']}>
          <select name="idUcenik">
            {ucenici.map((ucenik) => {
              return (
                <option>
                  {ucenik.ime} {ucenik.prezime}
                </option>
              );
            })}
          </select>
          <button>Dodaj ucenika u razred</button>
        </from>
      </>
    )
  );
};

export default AddUcenikURazredForm;
