import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useParams, useNavigate } from 'react-router-dom';
import { ucitajUcenika } from '../redux/ucenici/actions';
import Tabs from './Tabs';
import UcenikRow from './UcenikRow';

import {
  /*  BrowserRouter as Router,
  Routes,
  Switch,
  Route, */
  //useLocation,
  Outlet,
} from 'react-router-dom';
import { ucitajRazred } from '../redux/odjeljenjaRazredi/actions';

const Ucenik = (props) => {
  const { idUcenik } = useParams();
  const { idOdjeljenja } = useParams();
  const dispatch = useDispatch();
  const navigate = useNavigate();
  //let { path, url } = useRouteMatch();

  //const { pathname } = useLocation();

  //console.log(pathname);

  const [ucenik] = useSelector((state) =>
    state.ucenici.items.filter((ucenik) => ucenik.idUcenik === +idUcenik)
  );
  const [ucenikoviRazredi] = useSelector((state) =>
    state.razredi.items.filter(
      (ucenikoviRazredi) => ucenikoviRazredi.idOdjeljenja === +idOdjeljenja
    )
  );
  console.log(ucenik, ucenikoviRazredi);

  useEffect(() => {
    dispatch(ucitajUcenika(+idUcenik));
    dispatch(ucitajRazred(+idUcenik));
  }, []);

  if (!ucenik) {
    return <h1>Ucenik doesn't exist</h1>;
  }

  return (
    <>
      <UcenikRow
        id={ucenik.idUcenik}
        ime={ucenik.ime}
        prezime={ucenik.prezime}
        birthday={ucenik.birthday}
      />
      <Tabs razredi={ucenik.razredi} />
      <Outlet />
    </>
  );
};

export default Ucenik;
