import React from 'react';
import {
  createSearchParams,
  useNavigate,
  useSearchParams,
} from 'react-router-dom';
import { Tab, Tabs as TabsComponent, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';

const Tabs = ({ razredi, predmeti }) => {
  razredi.reverse();

  const [searchParams, setSearchParams] = useSearchParams();
  console.log(searchParams);
  const navigate = useNavigate();
  const params = [
    ['sort', 'date'],
    ['order', 'newest'],
  ];
  const goToPosts = () =>
    navigate({
      /* pathname: '/posts', */
      search: `?${createSearchParams(params)}`,
    });
  return (
    <>
      <TabsComponent>
        <TabList>
          {razredi.map((razred, i) => (
            <Tab onClick={goToPosts} key={i}>
              {razred.razred}
            </Tab>
          ))}
        </TabList>
        {razredi.map((razred, i) => (
          <TabPanel key={i}>
            <h2>Razrednik: {razred.razrednik}</h2>
            <TabsComponent>
              <TabList>
                {predmeti.map((predmet, i) => {
                  if (razred.razred === predmet.razred) {
                    return <Tab key={i}>{predmet.naziv}</Tab>;
                  }
                  return [];
                })}
              </TabList>
              {predmeti.map((predmet, i) => {
                if (razred.razred === predmet.razred) {
                  return (
                    <TabPanel key={i}>
                      <h2>Ocjena: {i}</h2>
                    </TabPanel>
                  );
                }
                return [];
              })}
            </TabsComponent>
          </TabPanel>
        ))}
      </TabsComponent>
    </>
  );
};

export default Tabs;
