import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useParams, useNavigate, Link, Outlet } from 'react-router-dom';
import { ucitajUcenikoveRazrede } from '../redux/odjeljenjaRazredi/actions';
import { Tab, Tabs as TabsComponent, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import { ucitajUcenikovePredmete } from '../redux/predmeti/actions';

const Razred = (props) => {
  const { idRazred } = useParams();
  const { idUcenik } = useParams();
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { loading } = useSelector((state) => state.predmeti);
  const ucenikoviPredmeti = useSelector((state) => state.predmeti.items);

  useEffect(() => {
    if (ucenikoviPredmeti.length < 1) {
      dispatch(ucitajUcenikovePredmete(+idUcenik, +idRazred)).then(
        (ucenikoviPredmeti) => {
          navigate(
            '/ucenici/' +
              idUcenik +
              '/' +
              idRazred +
              ucenikoviPredmeti[0].idPredmet +
              '/ocjene'
          );
        }
      );
    }
  }, [dispatch, idUcenik, ucenikoviPredmeti]);

  if (loading) {
    return <h1>Loading...</h1>;
  }
  if (!ucenikoviPredmeti) {
    return <h1>Ucenikovi predmeti doesn't exist</h1>;
  }

  return (
    <>
      <TabsComponent>
        {idRazred && (
          <TabList>
            {ucenikoviPredmeti.map((razred, i) => (
              <Tab key={razred.idRazred} active={'true'}>
                <Link
                  to={
                    '/ucenici/' + idUcenik + '/' + razred.idRazred + '/predmeti'
                  }
                >
                  {razred.razred}
                </Link>
              </Tab>
            ))}
          </TabList>
        )}
        {ucenikoviPredmeti.map((razred, i) => (
          <TabPanel key={razred.idRazred}>
            Razrednik je: {razred.razrednik}
          </TabPanel>
        ))}
      </TabsComponent>
      <Outlet />
    </>
  );
};

export default Razred;
