import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { dodajUsera } from '../redux/users/actions';
import formStyles from './Form.module.css';

const RegisterUser = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [ime, setIme] = useState('');
  const [password, setPassword] = useState('');
  const [backdrop, setBackdrop] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState(null);

  /*   const submitHandler = (e) => {
    e.preventDefault();
   
    dispatch(dodajUsera({ ime, password }));
  }; */
  function handleSubmit(event) {
    event.preventDefault();
    setError(null);
    setMessage('');
  }
  function handleChange(event) {
    event.preventDefault();
    const value = event.target.value;
    if (value.includes('_')) setError('You cannot use an underscore');
    else setError(null);
    setMessage(value);
    dispatch(dodajUsera({ ime, password }));
  }
  return (
    <>
      {!backdrop && <div className={formStyles.backdrop}></div>}
      <form className={formStyles['form-control']} onSubmit={handleSubmit}>
        <div>
          <ul>
            <li>
              <label>Ime</label>
              <input
                value={ime}
                onChange={() => handleChange()}
                type="text"
                placeholder="Ime"
              />
              {error && (
                <label style={{ color: 'red' }} htmlFor="message">
                  {error}
                </label>
              )}
            </li>
            <li>
              <label>Password</label>
              <input
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                type="password"
                placeholder="Password"
              />
            </li>
          </ul>
        </div>
        <button type="submit">Sign up</button>
      </form>
    </>
  );
};

export default RegisterUser;
